const headerEl=document.querySelector(".navigation");window.addEventListener("scroll",(()=>{window.scrollY>80?headerEl.classList.add("nav-scrolled"):window.scrollY<=80&&headerEl.classList.remove("nav-scrolled")})),document.querySelectorAll('a[href^="#"]').forEach((o=>{o.addEventListener("click",(function(o){o.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})}))}));const accordions=document.querySelectorAll(".accordion"),openAccordion=o=>{const c=o.querySelector(".accordion__content");o.classList.add("accordion__active"),c.style.maxHeight=c.scrollHeight+"px"},closeAccordion=o=>{const c=o.querySelector(".accordion__content");o.classList.remove("accordion__active"),c.style.maxHeight=null};accordions.forEach((o=>{const c=o.querySelector(".accordion__intro"),e=o.querySelector(".accordion__content");c.onclick=()=>{e.style.maxHeight?closeAccordion(o):(accordions.forEach((o=>closeAccordion(o))),openAccordion(o))}}));